CREATE MEMORY TABLE clients (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, sourname VARCHAR(32), name VARCHAR(32), middlename VARCHAR(32), tel VARCHAR(18))  
CREATE MEMORY TABLE orders (id BIGINT GENERATED BY DEFAULT AS IDENTITY PRIMARY KEY, client_id BIGINT, description VARCHAR(1024), start_date DATE, end_date DATE, price FLOAT, type INTEGER, FOREIGN KEY (client_id) REFERENCES clients(id) ON DELETE RESTRICT)
